	deleted = 1
	, delete_time = '${@java.time.LocalDateTime@now()}'
	, delete_type = #{delete_type}
	, delete_user = '${@cn.karelian.kas.utils.LoginInfomationUtil@getUserName()}'
WHERE id IN (

		#{id}

)

mapper.update:
